	<div>		<div class="ui-widget">			<label for="{hash}">{label}</label>			<input type="text" name="{name}" id="{hash}" />		</div>	</div>	<style>	.ui-autocomplete-loading { background: white url(<?php _a('css/ui-lightness/images/ui-anim_basic_16x16.gif'); ?>) right center no-repeat; }	</style>	<script>	$(function() {		var cache = {},			lastXhr;		$( "#{hash}" ).autocomplete({			minLength: 2,			source: function( request, response ) {				var term = request.term;				if ( term in cache ) {					response( cache[ term ] );					return;				}				lastXhr = $.getJSON( "/bls_cow/item/ajax/autocomplete/loc/city", request, function( data, status, xhr ) {					cache[ term ] = data;					if ( xhr === lastXhr ) {						response( data );					}				});			}		});	});	</script>